// Prompt node: take CURRENT Gmail item and prepare data for Ollama

// 1. Get the current input email (comes from Gmail Trigger)
const email = items[0].json;

// 2. Safely extract fields
const from = email.from || "";
const to = email.to || "";
const subject = email.subject || "";

const body =
  (email.body && email.body.plainText) ||
  email.textPlain ||
  email.snippet ||
  "";

// 3. Build prompt text for Ollama
const promptText = `
You are an email auto-reply assistant.

Email from: ${from}
Subject: ${subject}

Body:
${body}
`.trim();

// 4. Return ONE item that downstream nodes (Ollama, Response, Sheets) use
return [
  {
    json: {
      from,
      to,
      subject,
      body,          // <- this is what we log as original_body
      messageId: email.messageId || email.id || "",
      threadId: email.threadId || email.thread_id || "",
      promptText,    // <- use this in Ollama node as the message
    },
  },
];

// ==========================================
// Response node: choose template by category
// ==========================================
// Expects Ollama Chat Model to return JSON:
// { "category": "Sales", "confidence": 0.82 }
//
// Uses templates per category and outputs:
// { replyText, category, confidence, from, subject, threadId, messageId }
//
// Node names:
const OLLAMA_NODE_NAME = "Ollama Chat Model";
const PROMPT_NODE_NAME = "Prompt";

// ----- 1. Templates "file" (config) -----
const templates = {
  Sales: [
    "Thanks for reaching out! This is an automated reply, but our team will contact you as soon as possible. Glad to hear from you, sir.",
  ],
  Support: [
    "Thanks for contacting support. This is an automated acknowledgement — we’ve received your message and our team will get back to you shortly.",
  ],
  Complaint: [
    "We’re sorry to hear about this experience. This is an automated reply, but your message has been forwarded to our team for review.",
  ],
  General: [
    "Thanks for your email. This is an automated acknowledgement — we’ve received your message and will respond as soon as possible.",
  ],
  // Fallback template
  Default: [
    "Thank you for your email. This is an automated reply confirming that we’ve received your message.",
  ],
};

// ----- 2. Helpers -----
function safeNodeJson(name) {
  try {
    if ($node && $node[name] && $node[name].json) return $node[name].json;
  } catch (e) {}
  return null;
}

function tryParseJson(s) {
  if (typeof s !== "string") return null;
  try {
    return JSON.parse(s);
  } catch (e) {
    return null;
  }
}

function pickTemplate(category) {
  const key = category && templates[category] ? category : "Default";
  const arr = templates[key];
  if (Array.isArray(arr) && arr.length > 0) {
    const idx = Math.floor(Math.random() * arr.length);
    return arr[idx];
  }
  return "Thank you for your email.";
}

// ----- 3. Get model output & metadata -----
const modelData =
  safeNodeJson(OLLAMA_NODE_NAME) || // normal case
  items?.[0]?.json ||               // fallback
  {};

const meta = safeNodeJson(PROMPT_NODE_NAME) || items?.[0]?.json || {};

// If Ollama returned as string, parse JSON:
let classifier = modelData;
if (typeof modelData === "string") {
  const parsed = tryParseJson(modelData);
  if (parsed) classifier = parsed;
}

// Some n8n AI nodes put response under .text
if (!classifier.category && typeof modelData.text === "string") {
  const parsed = tryParseJson(modelData.text);
  if (parsed) classifier = parsed;
}

// ----- 4. Determine category & template -----
const category =
  (classifier.category || "General").toString().trim() || "General";
const confidence = typeof classifier.confidence === "number"
  ? classifier.confidence
  : null;

const replyText = pickTemplate(category);

// ----- 5. Build final output for Gmail + Sheets -----
const out = {
  replyText,
  category,
  confidence,
  from:
    meta.from ||
    meta.fromEmail ||
    meta.sender ||
    (meta.emailBody && meta.emailBody.from) ||
    "",
  subject: meta.subject || "",
  threadId:
    meta.threadId ||
    meta.thread_id ||
    meta.id ||
    meta.messageId ||
    "",
  messageId: meta.messageId || meta.id || "",
};

// Return one item
return [{ json: out }];
